LEN_TERR = 2500


class Helper_terrain:
    def __init__(self, terrain):
        # if type(terrain) == str:
        #     terrain = list(terrain)
        self.terrain = terrain

    def get_four_bounds(self):
        """

        :return:
        """

        top = self.terrain[:50]
        bottom = self.terrain[-50:]
        left = [self.terrain[i] for i in range(LEN_TERR) if i % 50 == 0]
        right = [self.terrain[i] for i in range(LEN_TERR) if i % 50 == 49]
        return {
            "top": top,
            "bottom": bottom,
            "left": left,
            "right": right
        }

    def get_dict_exit_count_per_dir(self):
        exit_info = {}
        four_bounds = self.get_four_bounds()
        for _dir, bound in four_bounds.items():
            if '0' in bound:
                exit_info[_dir] = bound.count('0')
        return exit_info

    def get_exit_dirs_name(self):
        return self.get_dict_exit_count_per_dir().keys()

    def get_exit_dirs_count(self):
        return len(self.get_dict_exit_count_per_dir().keys())


if __name__ == '__main__':
    ter = ''
    print(len(ter))
    helper = Helper_terrain(ter)
    print(helper.get_dict_exit_count_per_dir())
    print(helper.get_exit_dirs_count())
